import React, { Component } from 'react';
import axios from 'axios';

class GetDetails extends Component {
  constructor(props){
    super(props);
    this.state = {
      groupname: '',
      outputstatus: 0,
      output: {}
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSendExpenseDetails = this.handleSendExpenseDetails.bind(this);
  }

  handleChange(e) {
    var text = e.target.id;
    this.setState({[text]: e.target.value});
  }

  handleSendExpenseDetails(e){
    e.preventDefault();
    const reactData = {groupname: this.state.groupname};
    console.log(reactData);

    const url = '//localhost:5000/api/getdetails';

    axios.post(url,
      reactData,
      {headers: { 'content-type':'application/x-www-form-urlencoded', 'accept':'application/json' }})
       .then(res => this.setState({
            outputstatus: 1,
            output: res.data
       }))
       .catch(err => console.log(err.data))
  }

  render() {
    console.log(this.state);
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2 col-lg-3"></div>
          <div className="col-sm-12 col-md-8 col-lg-6">

              <form className="text-center border border-light p-5">
                <p className="h4 mb-4">Get Details of All Groups</p>

                <input
                  type="text"
                  id="groupname"
                  value={this.state.groupname}
                  className="form-control mb-4"
                  placeholder="Group Name"
                  onChange={this.handleChange}/>

                <button onClick={this.handleSendExpenseDetails} className="btn btn-info btn-block my-4" type="create">Check Group Details</button>
              </form>

              { this.state.outputstatus==1 ? (
                <div>
                  <p><strong>Group Name:</strong> {this.state.output.groupname} ({this.state.output.groupid})</p>
                  <p><strong>Group Members:</strong>

                  {this.state.output.groupmembers.map((i,key) => {
                    return(
                      {i}
                    )
                  })}

                  </p>
                </div>
              ): ""}

          </div>
        </div>
      </div>
    );
  }
}

export default GetDetails;
