import React, { Component } from 'react';

class Cards extends Component {
  constructor(props){
    super(props);
    this.state = {
      inputCount: ['member1','member2','member3']
    }
    this.handleAddInputField = this.handleAddInputField.bind(this);
  }

  handleAddInputField(e) {
    e.preventDefault();
    console.log('The link was clicked.');
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-sm-12 col-md-8 col-lg-6">

              <form className="text-center border border-light p-5">
                <p className="h4 mb-4">Create New Business Trip Group</p>
                <input type="text" id="groupname" className="form-control mb-4" placeholder="Group Name" />
                {this.state.inputCount.map((i,key) => {
                  return(
                    <input type="text" id={i} key={key} className="form-control mb-4" placeholder="Add Member Email" />
                  )
                })}
                  <button onClick={this.handleAddInputField} type="button" class="btn btn-default btn-sm">
                    <span className="glyphicon glyphicon-plus-sign"></span> Add More Members +
                  </button>
                <button className="btn btn-info btn-block my-4" type="create">Create Group</button>
              </form>

          </div>
        </div>
      </div>
    );
  }
}

export default Cards;
