import React, { Component } from 'react';
import axios from 'axios';

class AddExpense extends Component {
  constructor(props){
    super(props);
    this.state = {
      groupname: '',
      expense: '',
      totalamount: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleAddInputField = this.handleAddInputField.bind(this);
    this.handleSendGroupDetails = this.handleSendGroupDetails.bind(this);
  }

  handleChange(e) {
    var text = e.target.id;
    this.setState({[text]: e.target.value});
  }

  handleAddInputField(e) {
    e.preventDefault();
    this.setState({
      inputCount: [...this.state.inputCount, 'member'+(this.state.inputCount.length+1)],
      ['member'+(this.state.inputCount.length+1)]: ''
    })
  }

  handleSendGroupDetails(e){
    e.preventDefault();
    console.log(e);
    const reactData = {groupname: this.state.groupname};

    for (var i=0;i<this.state.inputCount.length;i++){
      var temp = this.state.inputCount[i];
      reactData[temp] = this.state[temp];
    }
    console.log(reactData);
    const url = '//localhost:5000/';

    axios.post(url,
      reactData,
      {headers: { 'content-type':'application/x-www-form-urlencoded', 'accept':'application/json' }})
       .then(res => console.log('Data send'))
       .catch(err => console.log(err.data))
  }

  render() {
    console.log(this.state);
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2 col-lg-3"></div>
          <div className="col-sm-12 col-md-8 col-lg-6">

              <form onSubmit={this.handleSendGroupDetails} className="text-center border border-light p-5">
                <p className="h4 mb-4">Add Expense to a Group</p>

                <input
                  type="text"
                  id="groupname"
                  value={this.state.groupname}
                  className="form-control mb-4"
                  placeholder="Group Name"
                  onChange={this.handleChange}/>

                <input
                  type="text"
                  id="expense"
                  value={this.state.expense}
                  className="form-control mb-4"
                  placeholder="Expense Heading"
                  onChange={this.handleChange}/>

                  <input
                    type="text"
                    id="totalamount"
                    value={this.state.totalamount}
                    className="form-control mb-4"
                    placeholder="Total Cost"
                    onChange={this.handleChange}/>

                <button className="btn btn-info btn-block my-4" type="create">Create Group</button>
              </form>

          </div>
        </div>
      </div>
    );
  }
}

export default AddExpense;
